mapper.select:
SELECT IFNULL(SUM(TIMESTAMPDIFF(MINUTE, create_time, end_time)), 0) AS totalMinutes
FROM biz_consultation_sessions
WHERE user_id = #{userId}
  AND eval_mode = #{evalMode}

SELECT bcr.session_id AS session_id, su.nick_name AS nick_name, bc.case_name AS case_name, bcs.step_name AS step_name, IFNULL(bce.totle_score, '未评分') AS score, CASE WHEN bce.totle_score >= 90 THEN '优' WHEN bce.totle_score >= 80 AND bce.totle_score < 90 THEN '良' WHEN bce.totle_score < 80 AND bce.totle_score >= 0 THEN '中' ELSE '无评级' END AS level, bcr.create_time AS start_time, bcr.end_time AS end_time, TIMESTAMPDIFF(MINUTE, bcr.create_time, bcr.end_time) AS totle FROM biz_consultation_sessions AS bcr LEFT JOIN sys_user AS su ON bcr.user_id = su.user_id LEFT JOIN biz_case AS bc ON bcr.case_id = bc.case_id LEFT JOIN biz_case_step AS bcs ON bcr.step_id = bcs.step_id LEFT JOIN biz_case_evaluations AS bce ON bcr.session_id = bce.session_id WHERE bcr.user_id = #{userId} AND bcr.eval_mode = #{evalMode}
and case_name like concat('%', #{caseName}, '%')
ORDER BY bcr.create_time DESC

SELECT COUNT(*) AS exam_count
FROM biz_consultation_sessions
WHERE user_id = #{userId}
  AND eval_mode = #{evalMode}
